package aca.puestos;

public class PuestoRH {

	int id_seccion;
	int id_categoria;
	int puesto_superior;
	String objetivo_puesto;
	String experiencia;
	String edad_maxima;
	String sexo;
	String nivel_ingles;
	String estudios;
	String avance_estudios;

	
	public PuestoRH() {
		// TODO Auto-generated constructor stub
	}


	public int getId_seccion() {
		return id_seccion;
	}


	public void setId_seccion(int id_seccion) {
		this.id_seccion = id_seccion;
	}


	public int getId_categoria() {
		return id_categoria;
	}


	public void setId_categoria(int id_categoria) {
		this.id_categoria = id_categoria;
	}


	public int getPuesto_superior() {
		return puesto_superior;
	}


	public void setPuesto_superior(int puesto_superior) {
		this.puesto_superior = puesto_superior;
	}


	public String getObjetivo_puesto() {
		return objetivo_puesto;
	}


	public void setObjetivo_puesto(String objetivo_puesto) {
		this.objetivo_puesto = objetivo_puesto;
	}


	public String getExperiencia() {
		return experiencia;
	}


	public void setExperiencia(String experiencia) {
		this.experiencia = experiencia;
	}


	public String getEdad_maxima() {
		return edad_maxima;
	}


	public void setEdad_maxima(String edad_maxima) {
		this.edad_maxima = edad_maxima;
	}


	public String getSexo() {
		return sexo;
	}


	public void setSexo(String sexo) {
		this.sexo = sexo;
	}


	public String getNivel_ingles() {
		return nivel_ingles;
	}


	public void setNivel_ingles(String nivel_ingles) {
		this.nivel_ingles = nivel_ingles;
	}


	public String getEstudios() {
		return estudios;
	}


	public void setEstudios(String estudios) {
		this.estudios = estudios;
	}


	public String getAvance_estudios() {
		return avance_estudios;
	}


	public void setAvance_estudios(String avance_estudios) {
		this.avance_estudios = avance_estudios;
	}

	
	
}
