package aca.puestos;

public class PuestosCCosto {

	int id_seccion;
	String ccosto;
	String responde_puesto;
	String informacion_manejada;
	String viaja;
	String horario;
	String periodos_especiales;
	String condiciones_especiales;

	
	public PuestosCCosto() {
		// TODO Auto-generated constructor stub
	}


	public int getId_seccion() {
		return id_seccion;
	}


	public void setId_seccion(int id_seccion) {
		this.id_seccion = id_seccion;
	}


	public String getCcosto() {
		return ccosto;
	}


	public void setCcosto(String ccosto) {
		this.ccosto = ccosto;
	}


	public String getResponde_puesto() {
		return responde_puesto;
	}


	public void setResponde_puesto(String responde_puesto) {
		this.responde_puesto = responde_puesto;
	}


	public String getInformacion_manejada() {
		return informacion_manejada;
	}


	public void setInformacion_manejada(String informacion_manejada) {
		this.informacion_manejada = informacion_manejada;
	}


	public String getViaja() {
		return viaja;
	}


	public void setViaja(String viaja) {
		this.viaja = viaja;
	}


	public String getHorario() {
		return horario;
	}


	public void setHorario(String horario) {
		this.horario = horario;
	}


	public String getPeriodos_especiales() {
		return periodos_especiales;
	}


	public void setPeriodos_especiales(String periodos_especiales) {
		this.periodos_especiales = periodos_especiales;
	}


	public String getCondiciones_especiales() {
		return condiciones_especiales;
	}


	public void setCondiciones_especiales(String condiciones_especiales) {
		this.condiciones_especiales = condiciones_especiales;
	}

	
}
